import './index.css';
import {marked} from 'marked';
import { useState } from "react";


export default function App() {
  const [markdown, setMarkdown] = useState(`
  #HELLO MARKDOWN !`);
  function change(e){
    setMarkdown(e.target.value);
  }
  return (
    <html>
      <div className="container mx-auto bg-gray rounded-xl shadow border p-8 m-10">
        <div className="app">
          <textarea className="inline" onChange={change} value={markdown}/>
        </div>

          <div
            className="inline"
            dangerouslySetInnerHTML={{__html: marked(markdown)}}/>
      </div>
    </html>
  );
}
